INSERT INTO encuestas VALUES 
('E1', 'Riesgo de Personal', 'Riesgos Operativos derivados de violaciones intencionales de las normativas internas o de las leyes por parte del personal interno; fallas en la Administración o gerencia del RRHH; omisiones en el cumplimiento de las relaciones contractuales con los empleados; etc.'),
('E2', 'Riesgo de Procesos', 'Riesgos Operativos generados por fallas en la ejecución de los procesos; deficiencias o ausencias en las políticas, normativas internas o procedimientos; fallas en la disposición de recursos, entre otros.'),
('E3', 'Riesgo de Sistemas', 'Riesgos Operativos derivados de fallas en los Sistemas Bancarios, Aplicaciones, Herramientas y elementos informáticos que utiliza la institución.'),
('E4', 'Riesgo de Factores Externos', 'Riesgos Operativos generados por acciones mal intencionadas de terceras personas; eventos naturales o de interrupciones importantes en los servicios externos.'),
('E5', 'Riesgo Reputacional y Legal', 'Riesgos derivados de operaciones vinculadas con la legitimación de capitales o financiamiento al terrorismo; fallas en la atención a clientes; de violaciones o incumplimientos de contratos, normativas o leyes que de manera no intencionada, pueda cometerse en perjuicio de terceros relacionados.');
